可分配资源&quot;); for (int i = 0; i &lt; resource; i++) &#123; System.out.print(available[i]+&quot; | &quot;); &#125;System.out.println(&quot;
&quot;); Request(); //应该由具体函数操作调用 &#125; while (true); &#125; public static void Display()&#123; //输出矩阵 System.out.println(&quot;	|MAX |Alloc |Need |Flag&quot;); for (int i = 0; i &lt; process; i++) &#123; System.out.print(&quot;进程&quot;+i); for (int j = 0; j &lt;= 3*resource; j++) &#123; if(j%resource == 0) System.out.print(&quot;|&quot;); System.out.print(array[i][j]+&quot; &quot;); &#125; System.out.println(&quot;
&quot;); &#125; &#125; public static void Request()&#123; int sum = 0; int[] need = new int[request.length]; System.out.println(&quot;输入请求资源进程：&quot;); processId = Input.nextInt(); //指定进程名 System.out.println(&quot;输入请求资源数量：&quot;);//request for (int i = 0; i &lt; resource; i++) &#123; request[i] = Input.nextInt(); need[i] = array[processId][2*resource+i]; &#125; if(!bankersCompare(need,request))&#123; System.out.println(&quot;申请超需求，请重新输入&quot;); Request(); &#125;else&#123; for (int i = 0; i &lt; resource; i++) &#123;//此处对几大数组进行直接修改，需注意 available[i] -= request[i]; array[processId][i+resource] += request[i]; //allocation + request array[processId][i+2*resource] -= request[i]; //need - request &#125;//预分配若 need==0 available+。。。。 for (int i = 0; i &lt; resource; i++) &#123; sum += array[processId][2*resource+i]; //need求和 &#125; if(sum==0) &#123; for (int i = 0; i &lt; resource; i++) &#123; available[i] += array[processId][i];//由于对available进行了修改，所以加的是max &#125; &#125; //以上else内对合法的request直接进行了分配 //然后进行安全检查 SecurityCheck(processId); &#125; &#125; public static void SecurityCheck(int processId)&#123;//int[] request Scanner Input = new Scanner(System.in); int[] work = Arrays.copyOf(available, resource); //创建work数组 int[] resourceNum = new int[resource]; boolean flag1 = false; boolean flag2 = false; int sum = 0; int sum2 = 0; int sum3 = 0; int flag3 = 0; int numComplete = 0; int times = 0; for (int i = 0; i &lt; process; i++) &#123; array[i][3*resource] = 0; &#125; //安全检查前先排除已经完毕的进程 for (int i = 0; i &lt; process; i++) &#123; for (int j = 0; j &lt; resource; j++) &#123; sum2 += array[i][2*resource+j];//need求和若为0则运行完成 &#125; if(sum2 == 0) numComplete++; else sum2 = 0; &#125; flag3 = process - numComplete; while(!flag2)&#123; //此处遍历为了不断循环直到找到整个安全序列 times++; sum3 = 0; numComplete = 0; for (int i = 0; i &lt; process; i++) &#123; sum3 += array[i][3*resource];//flag求和 &#125; flag2 = sum3 == flag3;//此处检查是否全部运行过 for (int i = 0; i &lt; process; i++) &#123; //遍历检查 sum = 0; for (int k = 0; k &lt; resource; k++) &#123; need[k] = array[i][2*resource+k]; //设置一下need sum += array[i][2*resource+k]; //need求和看是否完成 &#125; if(sum &gt; 0)&#123; if(array[i][3*resource] == 0 &amp;&amp; bankersCompare(work,need))&#123; //判断目前进程i是否满足需求 for (int j = 0; j &lt; resource; j++) &#123; work[j] += array[i][resource+j]; //改变work,work + allocation &#125; array[i][3*resource] = 1; //改变标志，即finish System.out.print(&quot;(&quot;+i+&quot;)--&gt;&quot;); //从头开始找 &#125; &#125; &#125; if( times&gt;process-numComplete)&#123; flag1 = true; //此处两个flag，两种跳出机制 break; &#125;else flag1= false; &#125; System.out.println(&quot;
&quot;); if(flag1)&#123; System.out.println(&quot;&lt;&lt;不安全,已退回&gt;&gt;&quot;); for (int i = 0; i &lt; resource; i++) &#123; //此处对几大数组进行直接修改，需注意（退回操作） available[i] += request[i]; array[processId][i+resource] -= request[i]; array[processId][i+2*resource] += request[i]; &#125; &#125; else System.out.println(&quot;&lt;&lt;安全&gt;&gt;&quot;); for (int j = 0; j &lt; process; j++) &#123; array[j][3*resource] = 0; &#125; for (int i = 0; i &lt; resource; i++) &#123; System.out.print(available[i]+&quot; | &quot;); &#125;System.out.println(&quot;
&quot;); Display(); &#125; public static boolean bankersCompare(int[] a,int[] b)&#123; int flag = 0; for (int i = 0; i &lt; a.length; i++) &#123; if(a[i] &gt;= b[i]) flag++; else flag--; &#125; return flag == a.length; &#125;&#125;
